!function(document, window, $) {
    "use strict";
    var Site=window.Site;
    $(document).ready(function($) {
        var arrow = !1;
        if ($(window).width() > 900){
            arrow = !0;
        }

        Site.run(), 

        $(".slider-menu").slick( {
            dots:!0, 
            infinite:!0, //!1 
            speed:500, 
            cssEase: 'linear',
            arrows: arrow,
            slidesToShow:5, 
            slidesToScroll:5, 
            responsive:[ 
            {
                breakpoint:1024, 
                settings: {
                    slidesToShow: 4, 
                    slidesToScroll: 4, 
                    infinite: !0, 
                    dots: !0
                }
            }, 
            {
                breakpoint:600, 
                settings: {
                    slidesToShow: 3, 
                    slidesToScroll: 3
                }
            }, 
            {
                breakpoint:480, 
                settings: {
                    slidesToShow: 2, 
                    slidesToScroll: 2
                }
            }]
        })
    })
}

(document, window, jQuery);